          value={text}
          className="w-full h-full py-2 appearance-none outline-none shadow-none"
          inputClassName="bg-transparent text-gray-100"
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              e.preventDefault();
            }
          }}
        />
      </div>
